    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:
        String age = ageid.getText().trim();
        String phone_number = numberid.getText().trim();
  try {
                int ageVal = Integer.parseInt(age);
            
                long phoneVal = Long.parseLong(phone_number);

                // Validate int range manually
                if (ageVal < 0 || ageVal > 100) {
                    JOptionPane.showMessageDialog(null, "Age must be a realistic number (0-100).", "Invalid Age", JOptionPane.ERROR_MESSAGE);
                    return;
                }

                // Validate length of phone numbers (e.g., max 11 digits)
                if (String.valueOf(phoneVal).length() == 11) 
                        {
                    JOptionPane.showMessageDialog(null, "Phone numbers must be no more and less than 11 digits.", "Invalid Phone Number", JOptionPane.ERROR_MESSAGE);
                    return;
                        }
            }
                catch (NumberFormatException e) {
                    JOptionPane.showMessageDialog(null, "Age and phone numbers must be numeric and within valid range.", "Invalid Input", JOptionPane.ERROR_MESSAGE);
                    return;
            }  
        
         boolean okayna = true ;
       // TextFields
if (nameid.getText().isEmpty() || ageid.getText().isEmpty() || occupation1.getText().isEmpty()|| socmed1.getText().isEmpty()
        ||birthplace1.getText().isEmpty()||adress1.getText().isEmpty()) {
       okayna = false ;   
}
// DateChooser
if (calendar1.getDate() == null) {
    okayna = false ;   
}

// ComboBox
if (civilstatus1.getSelectedIndex() == 0||nationality1.getSelectedIndex() == 0) {
        okayna = false ;  
}

// RadioButtons (halimbawa: gender)
if (!maleradio1.isSelected() && !femaleradio1.isSelected()) {
    
    okayna = false ;
    
}
if (validid1.getIcon() == null || pic2x2.getIcon() == null) {
       okayna = false;
}


    if(okayna){
        namereceipt.setText(nameid.getText());
        agereceipt.setText(ageid.getText());
        numberreceipt.setText(numberid.getText());
       
        Random rand = new Random();
        int randomNum = 100 + rand.nextInt(900); // generates number between 100-999

        pollreceipt.setText(Integer.toString(randomNum));
        RECEIPT.setVisible(true);
        BARANGAY_ID.setVisible(false);
        
        // ==== CLEAR ALL FIELDS AFTER SUBMISSION ====
    nameid.setText("");
    ageid.setText("");
    occupation1.setText("");
    socmed1.setText("");
    birthplace1.setText("");
    adress1.setText("");
    numberid.setText("");
    nationality1.setSelectedIndex(0);
    calendar1.setDate(null); // clear JCalendar
    civilstatus1.setSelectedIndex(0); // reset combo box
    maleradio1.setSelected(false); // clear radio button
    femaleradio1.setSelected(false); // clear radio button
    validid1.setIcon(null);
    pic2x2.setIcon(null);
        
    } else {
    JOptionPane.showMessageDialog(null, "Please complete all required fields.");
}
        
        
        
       
        
        
    }//GEN-LAST:event_jButton2ActionPerformed

